import React from "react";
import '../assets/css/eventform.css'

class Eventregister extends React.Component{

    state = {
        fname: "",
        lname: "",
        phnumber: "",
        city: "",
        age: "",
        kids: ""
    }

    handleChangeFirstName = event => {
        this.setState({fname: event.target.value})
    }
    handleChangeLastName = event => {
        this.setState({lname: event.target.value})
    }
    handleChangePhone = event => {
        this.setState({phnumber: event.target.value})
    }
    handleChangeCity = event => {
        this.setState({city: event.target.value})
    }
    handleChangeAge = event => {
        this.setState({age: event.target.value})
    }
    handleChangeKids = event => {
        this.setState({kids: event.target.value})
    }

    handleSubmit = () => {
        console.log(this.state)
    }

    render() {
        return(
            <div className="eventForm">
                <div className="Desktop">Hello Desktop
                </div>
                <div className="Mobile">
                    <form>
                        <table>
                            <tr>
                                <td>
                                    <input type="text" value={this.state.fname} onChange={this.handleChangeFirstName} placeholder={"First Name"}/>
                                </td>
                                <td>
                                    <input type="text" value={this.state.lname} onChange={this.handleChangeLastName} placeholder={"Last Name"}/>
                                </td>
                            </tr>
                            <tr>
                                <td>
                                    <input type="number" value={this.state.phnumber} onChange={this.handleChangePhone} placeholder={"Phone Number"}/>
                                </td>
                            </tr>
                            <tr>
                                <td>
                                    <input type="text" value={this.state.city} onChange={this.handleChangeCity} placeholder={"City"}/>
                                </td>
                                <td>
                                    <input type="number" value={this.state.age} onChange={this.handleChangeAge} placeholder={"Age"}/>
                                </td>
                            </tr>
                            <tr>
                                <td>
                                    <input type="number" value={this.state.kids} onChange={this.handleChangeKids} placeholder={"Kids"}/>
                                </td>
                            </tr>
                        </table>
                        <button className="submit-button" onClick={this.handleSubmit}>Submit</button>
                    </form>
                </div>
            </div>
        );
    }
}

export default Eventregister;
